import { useState } from 'react'

const Tasks = () => {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            text: 'Turno con el médico',
            day: '5 de Febrero a las 2:30pm',
            reminder: true,
        },
        {
            id: 2,
            text: 'Reunión en la escuela',
            day: '6 de Febrero a las 1:30pm',
            reminder: true,
        },
        {
            id: 3,
            text: 'Compras',
            day: '5 de Febrero a las 4:30pm',
            reminder: false,
        }
    ])

    return (
        <>
          {tasks.map((task) => ( 
          <h3 key={task.id}>{task.text}</h3> 
          ))}  
        </>
    )
}

export default Tasks
